% Name - Sakshi Pandagale% Roll number - BT19ECE005% ASSIGNMENT 3 - Create two images, one with white circle in the middle%                and one with a white rectabngle in the middle and%                perform all logical operations on both the obtained imagesclc;close all;clear all;%% Creating image with white circle in the middlerad = 150;x=0;y=0;[X,Y]=ndgrid(-200:200, -200:200);C = (X-x).^2+(Y-y).^2 <= rad^2;%% Creating image with white rectangle in the middleR = zeros(401);s = size(R);for i = int16(3*s(1)/10):int16(7*s(1)/10)    for j = int16(2*s(2)/10):int16(8*s(2)/10)        R(i,j) = 1;            endend%% PERFORMING LOGICAL OPERATIONS ON BOTH IMAGESAND = C & R;OR = C | R;NAND = ~(C & R);NOR = ~(C | R);XOR = xor(C, R);XNOR = ~xor(C, R);subplot(4,2,1), imshow(C);subplot(4,2,2), imshow(R);subplot(4,2,3), imshow(AND), title('AND');subplot(4,2,4), imshow(OR), title('OR');subplot(4,2,5), imshow(NAND), title('NAND');subplot(4,2,6), imshow(NOR), title('NOR');subplot(4,2,7), imshow(XOR), title('XOR');subplot(4,2,8), imshow(XNOR), title('XNOR');